sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    //Limit the number of selected options in a list collector
    //Specify the max options and variable name below
    var maxOptions = 2;
    var collectorName = 'area';    
    var selectedOptions = g_form.getValue(collectorName).split(',');
    if (selectedOptions.length > maxOptions) {
		// debugger;
        //Remove the last item
        // myListCollector.removeItem(selectedOptions[selectedOptions.length - 1]);
        g_form.addErrorMessage('Non è possibile selezionare più di ' + maxOptions + ' aree.');

		// newValue = selectedOptions[0] + ", " + selectedOptions[1];		
		g_form.removeOption("select_0"+ control.id, selectedOptions[2] );
		//return false; 
    }

}
